import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles((theme) => ({
  container: {
    background: "linear-gradient(45deg, #cbab89 30%, #b3b4af 90%)",
    padding: theme.spacing(8, 0, 6),
  },
  icon: {
    marginRight: "20px",
  },
  buttons: {
    marginTop: "40px",
  },
  toolbar: {
    color: "#27201a",
    background: "linear-gradient(45deg, #605b59 30%, #e1d5c9 90%)",
  },
  buttonCommunity: {
    color: "#27201a",
    backgroundColor: "#e1d5c9",
  },
  buttonArtist: {
    color: "#27201a",
    backgroundColor: "#e1d5c9",
  },
}));

export default useStyles;
