import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles((theme) => ({
  sheet: {
    background: "linear-gradient(45deg, #cbab89 70%, #bc8c61 100%)",
  },
  toolbar: {
    color: "#27201a",
    backgroundColor: "#e1d5c9",
    borderColor: "#bc8c61",
  },
  icon: {
    marginRight: "20px",
  },
  container: {
    background: "linear-gradient(45deg, #cbab89 70%, #bc8c61 100%)",
    padding: "50px",
  },
  buttons: {
    marginTop: "40px",
  },
  buttonCommunity: {
    color: "#27201a",
    backgroundColor: "#e1d5c9",
    borderColor: "#bc8c61",
  },
  buttonArtist: {
    color: "#27201a",
    backgroundColor: "#e1d5c9",
    borderColor: "",
  },
  cardGrid: {
    padding: "20px 0",
  },
  card: {
    height: "100%",
    display: "flex",
    flexDirection: "column",
  },
  cardMedia: {
    paddingTop: "56.25%", // 16:9 ratio
  },
  cardContent: {
    flexGrow: 1,
  },
}));

export default useStyles;
